$(document).ready(function(){
    /*鼠标事件*/
    $(".nav-link").
    hover(
        function () {
            /*鼠标进入事件*/
            $(this).css({"background-color":"yellow","border-top-left-radius":"90px 20px","border-bottom-right-radius":"90px 20px"});
            $(this).attr({ class: "nav-link out"});
        },
        function () {
            //mouseout
            /* alert("超链接1");*/
            $(".out").css({"background-color":"#ebefee0d","border-radius":"0px"});
        }
    );
    /*点击事件*/
    $(".nav-link").click(function () {
        $(this).css({"background-color":"yellow","border-top-left-radius":"90px 20px","border-bottom-right-radius":"90px 20px"});
        $(".navigation-click").css({"background-color":"#ebefee0d","border-radius":"0px"});
        $(this).attr({ class: "nav-link navigation-click"});
    });
});